  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.25);
  position: absolute;
  top: 0;
  left: 0;
  z-index: 9999;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  visibility: hidden;
  transition: all 0.5s;
  /* DOUBLE SPINNER */ }
  .main-overlay.visible {
    visibility: visible;
    transition: all 0.5s; }
  .main-overlay .loader {
    width: 100px;
    height: 100px;
    box-sizing: border-box;
    margin: 0;
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden; }
  .main-overlay .double-spinner {
    position: relative; }
  .main-overlay .dbl-spinner {
    position: absolute;
    width: 75px;
    height: 75px;
    border-radius: 50%;
    background-color: transparent;
    border: 4px solid transparent;
    border-top: 4px solid #2b4590;
    border-left: 4px solid #2b4590;
    -webkit-animation: 2s spin linear infinite;
    animation: 2s spin linear infinite; }
  .main-overlay .dbl-spinner--2 {
    border: 4px solid transparent;
    border-right: 4px solid #ee885a;
    border-bottom: 4px solid #ee885a;
    -webkit-animation: 1s spin linear infinite;
    animation: 1s spin linear infinite; }

@keyframes spin {
  from {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg); }
  to {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg); } }
  .main-overlay .message {
    display: block;
    min-width: 20rem;
    max-width: 50%;
    height: auto;
    padding: 1rem;
    box-sizing: border-box;
    color: #fff;
    font-size: 1.4rem;
    text-align: center; }
